<script>Solidres.jQuery(document).ready(function($){
            var sessionLifeTime = 7200;
            var sessionTimeWarning = 600;
            var sessionTimeReload = 0;
            var warned = false;
            var countDown = function (time) {        				    
                var hours   = Math.floor(time / 3600);
                var minutes = Math.floor((time - (hours * 3600)) / 60);
                 var seconds = time - (hours * 3600) - (minutes * 60);
                 if (hours < 10) {hours = "0" + hours;}
                 if (minutes < 10) {minutes = "0" + minutes;}
                 if (seconds < 10) {seconds = "0" + seconds;}                            
                 return "<span class='h'>" + hours + "</span>"
                         + " <span class='m'>" + minutes + "</span>"
                         + " <span class='s'>" + seconds + "</span>";
            };
                    
            var popup = $("#session_expiration");
            
            popup.on("click", "button", function() {
                $(this).find(">.fa").addClass("fa-spin");
                window.location.reload();
            });
                    
            window.setInterval(function(){					
                if(sessionLifeTime <= sessionTimeWarning && !warned){		
                    warned = true;
                    popup.modal("show");
                }					
            }, 1000);
                    
            var interval = window.setInterval(function(){				
                if(sessionTimeReload > 0 && sessionLifeTime <= sessionTimeReload){
                    window.location.reload();
                }		
                if(sessionLifeTime < 1){
                    popup.find(".countdown, .btn").remove();
                    popup.find(".modal-header h3").html("Expired session warning");
                    popup.find(".modal-body").html("Your session has been expired, <a href=\"#\">click here</a> to start a new session.").find(">a").on("click", function(){
                        window.location.reload();
                    });
                    window.clearInterval(interval);
                }else{
                    popup.find(".countdown").html(countDown(--sessionLifeTime));
                }					
            }, 1000);
        });</script>
